import React from 'react';
import { useHistory } from 'react-router-dom';

function Home() {

  const [loaded, setLoaded] = useState(false);
  const history = useHistory();

  useEffect(() => {
      const unlisten = history.listen(() => {
        setLoaded(false);
        setTimeout(() => setLoaded(true), 100); // Delay setting loaded to true to allow transition to occur
      });
      return () => unlisten();
    }, [history]);

    return (
        <div className={`fade-in ${loaded ? 'active' : ''}`}>
            {<div className="Home">
                     <p className="homeParagraph">Welcome to my portfolio! This page exists to showcase my past, current, and future projects.</p>
                     <p className="homeParagraph">I'm a software developer who lives in the St. Louis, MO area. I have experience with modern web technologies such as HTML/CSS/JS, Angular, mySQL, as well as application programming languages such as Java and C++. I'm currently learning React which is what this page was made with. It will always be a work in progress.</p>
                     <p className="homeParagraph">Feel free to contact me with any questions you have, I'm always looking for new opportunities!</p>
                 </div>}
        </div>
    );

}

export default Home;
