import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

function Header() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isProjectsOpen, setIsProjectsOpen] = useState(false); // State for projects dropdown
    const location = useLocation();

    useEffect(() => {
        // Close the menu when the route changes
        setIsMenuOpen(false);
    }, [location]);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleProjects = () => {
        setIsProjectsOpen(!isProjectsOpen);
    };

    return (
        <nav className={`navbar${isMenuOpen ? ' open' : ''}`}>
            <div className="navbar-left">
                <h1 className="header-title">PORTFOLIO // Jonathan Rainey</h1>
            </div>
            <div className="navbar-right">
                <button className="hamburger-menu" onClick={toggleMenu}>
                    &#9776;
                </button>
            </div>
            <div className={`menu${isMenuOpen ? ' open' : ''}`}>
                <button className="close-button" onClick={toggleMenu}>
                    Ã—
                </button>
                <ul>
                    <li><Link to="/home">Home</Link></li>
                    <li><Link to="/resume">Resume</Link></li>
                    <li className="dropdown-item">
                        <div className="dropdown" onClick={toggleProjects}>
                            <span className="dropbtn bold-text">Projects</span>
                            {isProjectsOpen ? <span>&#9650;</span> : <span>&#9660;</span>} {/* Arrow */}
                            {isProjectsOpen && (
                                <div className="dropdown-content">
                                    <Link to="/project1">Project 1</Link>
                                    <Link to="/project2">Project 2</Link>
                                    <Link to="/project3">Project 3</Link>
                                </div>
                            )}
                        </div>
                    </li>
                    <li><Link to="/contact">Contact</Link></li>
                </ul>
            </div>

            <div className="color-blend-line"></div>
        </nav>
    );
}

export default Header;
