import React, { useState, useEffect } from 'react';

// Create a context to manage the fade-in state
const FadeInContext = React.createContext();

// Higher-order component to apply fade-in effect to pages
const FadeInPage = ({ children }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Simulate loading delay
    const delay = setTimeout(() => {
      setIsVisible(true);
      clearTimeout(delay);
    }, 500); // Adjust the delay time as needed

    // Clean up
    return () => clearTimeout(delay);
  }, []);

  return (
    <FadeInContext.Provider value={isVisible}>
      <div className="FadeInPage">
        {children}
      </div>
    </FadeInContext.Provider>
  );
};

export default FadeInPage;

// Custom hook to access the fade-in state
export const useFadeIn = () => React.useContext(FadeInContext);
