// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Home from './pages/Home';
import Resume from './pages/Resume';
import ExcelProject from './pages/ExcelProject';
import AlongTheWay from './pages/AlongTheWay';
import SportsDataProject from './pages/SportsDataProject';
import Contact from './pages/Contact';
import Header from './components/Header';
import LastCommitTimer from './components/LastCommitTimer';

function App() {
  const [showFooter, setShowFooter] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    const scrollTop = window.scrollY;
    const windowHeight = window.innerHeight;
    const bodyHeight = document.body.scrollHeight;
    const scrollPercentage = (scrollTop / (bodyHeight - windowHeight)) * 100;

    // Adjust the threshold as needed
    if (scrollPercentage > 90) {
      setShowFooter(true);
    } else {
      setShowFooter(false);
    }
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/home" element={<Home />} />
            <Route path="/resume" element={<Resume />} />
            <Route path="/excelproject" element={<ExcelProject />} />
            <Route path="/alongtheway" element={<AlongTheWay />} />
            <Route path="/sportsdataproject" element={<SportsDataProject />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </div>
        {showFooter && (
          <footer className="footer">
            <LastCommitTimer />
          </footer>
        )}
      </div>
    </Router>
  );
}

export default App;
