import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import './App.css';
import Home from './pages/Home';
import Resume from './pages/Resume';
import ExcelProject from './pages/ExcelProject';
import AlongTheWay from './pages/AlongTheWay';
import SportsDataProject from './pages/SportsDataProject';
import Contact from './pages/Contact';
import Header from './components/Header';
import LastCommitTimer from './components/LastCommitTimer';

function ScrollControl() {
  const location = useLocation();

  React.useEffect(() => {
    // Add or remove class based on the current location
    if (location.pathname === '/') {
      document.body.classList.add('no-scroll');
    } else {
      document.body.classList.remove('no-scroll');
    }
  }, [location]);

  return null; // No need to render anything
}

function App() {
  return (
    <Router>
      <div className="App">
        <ScrollControl /> {/* Render the ScrollControl component */}
        <Header />
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/home" element={<Home />} />
            <Route path="/resume" element={<Resume />} />
            <Route path="/excelproject" element={<ExcelProject />} />
            <Route path="/alongtheway" element={<AlongTheWay />} />
            <Route path="/sportsdataproject" element={<SportsDataProject />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </div>
        <footer>
          <LastCommitTimer />
        </footer>
      </div>
    </Router>
  );
}

export default App;
